Querys.query:

          	DROP TABLE IF EXISTS leaderboard;
			DROP TABLE IF EXISTS game;
			DROP TABLE IF EXISTS forum;
			DROP TABLE IF EXISTS product;
			DROP TABLE IF EXISTS forum_admin;
			DROP TABLE IF EXISTS product_admin;
			DROP TABLE IF EXISTS player;
			DROP TABLE IF EXISTS gameseller;
			DROP TABLE IF EXISTS user_;

CREATE TABLE product( productId int(10) NOT NULL PRIMARY KEY AUTO_INCREMENT, product_adminID int(10), product_price float, productTitle varchar(100), productImage varchar(500),
CONSTRAINT productfk1 FOREIGN KEY(product_adminID) REFERENCES forum_admin(forumAID)
)

CREATE TABLE leaderboard( leaderboardId int(10) NOT NULL PRIMARY KEY AUTO_INCREMENT, gameID int(10), playerID int(10), topscore float,
CONSTRAINT leaderboardfk1 FOREIGN KEY(gameID) REFERENCES game(gameID), CONSTRAINT leaderboardfk2 FOREIGN KEY(playerID) REFERENCES player(playerID)
)
